#include<iostream>using namespace std;int main(){	int i = 5;	int *ptr = &i; //making a ptr variable which is storing the address of i.	cout << ptr << endl;	cout << &i << endl; //& is "address of".	cout << "------------" << endl; 	cout << *ptr << endl; //* is "value at".	cout << &ptr << endl;	cout << "------------" << endl;	int arr[] = {1,2,3,4};	int *arrptr = &arr[1];    cout << arrptr << endl;	cout << arrptr + 1 << endl;	cout << "------------" << endl;	cout << *(arrptr + 1) << endl;	cout << *arrptr++ << endl;	cout << ++*arrptr << endl;	cout << "------------" << endl;	int a = 80;	int &x = a; //this is some sort of aliasing	cout << x << endl;	cout << &x << endl;	return 0;}